<?php

use lib\Log;
use lib\Rest;

use lib\Application;

require_once('lib/autoloader.php');
require_once('lib/application.php');
require_once('functions.php');

$application = new Application();
$application->run();

try {
    Log::deleteOldFiles(7, $_REQUEST['auth']['domain']);
} catch (Exception $e) {
    Log::add($e->getMessage(), $_REQUEST['auth']['domain']);
}

switch (strtoupper($_REQUEST['event'])) {
    case 'ONAPPINSTALL':

        $handlerBackUrl = ($_SERVER['SERVER_PORT'] == 443 ? 'https' : 'http') .
            '://' . $_SERVER['SERVER_NAME'] .
            (in_array($_SERVER['SERVER_PORT'], array(80, 443)) ? '' : ':' .
                $_SERVER['SERVER_PORT']) . $_SERVER['SCRIPT_NAME'];

        $result = Rest::restCommand('imbot.register', array(
            // unique bot identifier  (req.)
            'CODE' => 'LocalChatBotTransmitter',
            // Bot type
            'TYPE' => 'B',
            // Bot handler for new messages from user (req.)
            'EVENT_MESSAGE_ADD' => $handlerBackUrl,
            // Bot handler for joining to a chat (req.)
            'EVENT_WELCOME_MESSAGE' => $handlerBackUrl,
            // Bot handler for deleting bot (req.)
            'EVENT_BOT_DELETE' => $handlerBackUrl,
            // Bot personality (req.)
            'PROPERTIES' => array(
                // Bot name (NAME or LAST_NAME is required)
                'NAME' => '"Transmitter"',
                // Bot last name
                'LAST_NAME' => 'Chat Bot',
                // Bot color for mobile Bitrix24 application
                'COLOR' => 'GRAPHITE',
                'EMAIL' => 'baarlord@yandex.ru',
                // format YYYY-mm-dd
                'PERSONAL_BIRTHDAY' => '2020-06-27',
                // Bot 'job-title' as a bot description
                'WORK_POSITION' => 'I help with business',
                'PERSONAL_WWW' => 'https://github.com/baarlord/localChatBotTransmitter',
                // Bot gender
                'PERSONAL_GENDER' => 'M',
                // base64 bot avatar
                'PERSONAL_PHOTO' => '',
            ),
        ), $_REQUEST["auth"]);

        break;
    case 'ONIMBOTJOINCHAT':
        // send help message how to use chat-bot. For private chat and for group chat need send different instructions.
        $result = Rest::restCommand('imbot.message.add', array(
            'DIALOG_ID' => $_REQUEST['data']['PARAMS']['DIALOG_ID'],
            'MESSAGE' => 'Hi! I am an transmitter - bot. I\'ll duplicate what you write to the Department you choose',
        ), $_REQUEST["auth"]);

        break;

    case 'ONIMBOTMESSAGEADD':

        // response from our bot
        $arReport = getAnswer($_REQUEST['data']['PARAMS']['MESSAGE']);

        // send answer message
        $result = Rest::restCommand('imbot.message.add',
            array(
                'DIALOG_ID' => $_REQUEST['data']['PARAMS']['DIALOG_ID'],
                'MESSAGE' => $arReport['title'] . "\n" . $arReport['report'] . "\n",
            ),
            $_REQUEST["auth"]);

        break;
}
